
/**
 * 排序时返回字段和状态
 */
 declare interface SortByName {
    name: string;
    active: boolean;
}

declare interface LabelValue {
    label: string;
    value: string;
}

/**生成的api id为ref,找不到，所以在这里定义一个类型 */
type ref = string;

/** 接口请求返回的信息 */
declare class ResData<T> {
    /** code */
    code?: string;

    /** data */
    data?: T;

    /** dmessage */
    dmessage?: string;

    /** message */
    message?: string;
}

/** 登录用户的信息 */
declare interface UserInfo {
    commissionWeight: Number;
    createTime: Number;
    deptId: Number;
    email: string;
    gradeId: Number;
    isDelete: Number;
    isEdit: Number;
    isLogin: Number;
    isNew: Number;
    jobId: Number;
    jobName: string;
    mobile: string;
    name: string;
    password: string;
    rolesId: string;
    salt: string;
    sex: Number;
    staffUserCode: string;
    staffUserId: Number;
    status: Number;
    storeNumber: string;
    username: string;
    workStatus: Number;
    storeName:string;

}
